mapper.select:
select * from SYS_MATERIAL t
where 1=1

    and (
    t.GOODS_NAME LIKE (concat('%',concat(#{goodsName,jdbcType=VARCHAR},'%')))
    AND t.GOODS_NAME >' '
    )


    and t.GOODS_TYPE = #{goodsType,jdbcType=VARCHAR}


    and t.GOODS_SPECIFICATION = #{goodsSpecification,jdbcType=VARCHAR}

limit #{start,jdbcType=DECIMAL},#{end,jdbcType=DECIMAL}

select count(1)
from SYS_MATERIAL t
where 1=1

    and (
    t.GOODS_NAME LIKE (concat('%',concat(#{goodsName,jdbcType=VARCHAR},'%')))
    AND t.GOODS_NAME >' '
    )


    and t.GOODS_TYPE = #{goodsType,jdbcType=VARCHAR}


    and t.GOODS_SPECIFICATION = #{goodsSpecification,jdbcType=VARCHAR}
